Format.getInstance();//        String prezzo;        Double prezzo;        //prima dovrei controllare i prezzi inseriti        //prima elimino i prezzi da reinserire        sql = "delete from articoli_prezzi";        sql += " where articolo = " + Db.pc(this.texCodi.getText(), java.sql.Types.VARCHAR);        if (Db.executeSql(sql) == true) {            for (int i = 0; i < tabListino.getRowCount(); i++) {                try {                    Object val = tabListino.getModel().getValueAt(i, 7);   //prendo il nuovo campo prezzo calcolato o modificato                    if (val instanceof BigDecimal) {                        prezzo = ((BigDecimal) tabListino.getModel().getValueAt(i, 7)).doubleValue();                    } else if (val instanceof Double) {                        prezzo = (Double) tabListino.getModel().getValueAt(i, 7);                    } else {                        prezzo = Db.getDouble(tabListino.getModel().getValueAt(i, 7).toString());                    }                } catch (Exception err) {                    err.printStackTrace();                    prezzo = 0d;                }                System.out.println(tabListino.getValueAt(i, 0) + " : " + prezzo);                Double sconto1 = CastUtils.toDouble(tabListino.getModel().getValueAt(i, 8));                Double sconto2 = CastUtils.toDouble(tabListino.getModel().getValueAt(i, 9));                sql = "insert into articoli_prezzi (articolo, listino, prezzo, sconto1, sconto2) values (";                sql += Db.pc(this.texCodi.getText(), java.sql.Types.VARCHAR);                sql += ", " + Db.pc(tabListino.getValueAt(i, 0), java.sql.Types.VARCHAR);                sql += ", " + Db.pc(prezzo, java.sql.Types.DECIMAL);                sql += ", " + Db.pc(sconto1, java.sql.Types.DECIMAL);                sql += ", " + Db.pc(sconto2, java.sql.Types.DECIMAL);                sql += ")";                Db.executeSql(sql);            }        } else {    